mapper.select:
    select

     from
     	area
     where
     	delete_flag=1


	        and
	        	area_address = #{areaAddress,jdbcType=VARCHAR}


	        and
	        	person_id = #{personId,jdbcType=VARCHAR}


	        and
	        	rent_person_id = #{rentPersonId,jdbcType=VARCHAR}


	        and
	        	area_confirm = #{areaConfirm,jdbcType=INTEGER}


	        and
	        	area_rent = #{areaRent,jdbcType=INTEGER}

	  limit #{start},#{end}

    select
    	count(id)
     from
     	area
     where
     	delete_flag=1


	        and
	        	area_address = #{areaAddress,jdbcType=VARCHAR}


	        and
	        	person_id = #{personId,jdbcType=VARCHAR}


	        and
	        	rent_person_id = #{rentPersonId,jdbcType=VARCHAR}


	        and
	        	area_confirm = #{areaConfirm,jdbcType=INTEGER}


	        and
	        	area_rent = #{areaRent,jdbcType=INTEGER}
